import { connect } from 'mqtt';
import { Client } from 'paho-mqtt';
import React, { useState } from 'react';
import Select from 'react-select';
import './App.css';
import ChartViewer from "./ChartViewer";



function App() {
  const options = [
    { value: 'aq1', label: 'Tiva 1' },
    { value: 'aq2', label: 'Tiva 2' },
    { value: 'aq3', label: 'Tiva 3' },
    { value: 'aq2', label: 'Tiva 4' },
    { value: 'aq3', label: 'Tiva 5' }
  ];

  const options2 = {
    // Clean session
    port: 1883,
  }

  // const client = connect('mqtt://broker.emqx.io',options2);
  //const cliente = connect('mqtt://broker.emqx.io',options2);
  const cliente = new Client('ws://broker.emqx.io:1883/', 'energia-sample');

  cliente.onConnectionLost = (responseObject) => {
    console.log('Connection lost:', responseObject.errorMessage);
  };

  cliente.onMessageArrived = (message) => {
    console.log('Received message:', message.payloadString);
  };
  if (cliente.isConnected) {
    console.log('MQTT client is connected');
  } else {
    console.log('MQTT client is not connected');
  }

  const topic1 = "mqtt/ufpb-aq1/temp";
  // // Subscribe to a topic



  cliente.subscribe(topic1, { qos: 1 });

  // const teste = client.subscribe(topic1, { qos: 1 });
  // console.log(teste);
  const batteryLevel  = 95;
  const batteryColor = batteryLevel <= 90 ? 'red' : 'green';

  const currentDate = new Date();

  const [dataSetOne, updateDataSetOne] = useState([0, 0, 0, 0, 0, 0]);

  const [dataSetTwo, updateDataSetTwo] = useState([0, 0, 0, 0, 0, 0]);

  // add side effect to component
  React.useEffect(() => {
    // create interval
    const interval = setInterval(
      // set number every 5s
      () => {
        const val1 = Math.floor(Math.random() * (50 - 0 + 1)) + 0;
        updateDataSetOne(prevData => {
          if(prevData.length >= 20) prevData.shift();
          console.log(prevData);
          return ([...prevData, val1]);
        });

        const val2 = Math.floor(Math.random() * (90 - 20 + 1)) + 20;
        updateDataSetTwo(prevData => {
          if(prevData.length >= 20) prevData.shift();
          console.log(prevData);
          return ([...prevData, val2]);
        });
      },
      500
    );
    // clean up interval on unmount
    return () => {
      clearInterval(interval);
    };

  }, []);
  return (
    <div className="App">
      <header className="App-header">
        <h1>UFPB</h1>
      </header>
      <div className='main'>
        <div className='menus'>
          <div className='node-wrapper'>
            <label htmlFor='nodes'>Node:</label>
            <Select options={options} defaultValue={options[0]} id="nodes"></Select>
          </div>
          <div className='battery' style={{ backgroundColor: batteryColor }}>
            <h3>Battery: {batteryLevel}%</h3>
          </div>
        </div>
        <div className='data'>
          <ChartViewer data={dataSetOne} title="Temperatura" />
          <ChartViewer data={dataSetTwo} title="Humidade" />
        </div>
      </div>
    </div>
  );
}

export default App;
