// import { Client } from 'paho-mqtt';
import { connect, MqttClient } from 'mqtt'
import React, { useState } from 'react';
import Select from 'react-select';
import './App.css';
import ChartViewer from "./ChartViewer";



function App() {
  const options = [
    { value: 'aq1', label: 'Tiva 1' },
    { value: 'aq2', label: 'Tiva 2' },
    { value: 'aq3', label: 'Tiva 3' },
    { value: 'aq2', label: 'Tiva 4' },
    { value: 'aq3', label: 'Tiva 5' }
  ];

  const [incomingMessage, setIncomingMessage] = useState(null);
  const batteryLevel  = 95;
  const client = connect('wss://broker.emqx.io:8084/mqtt');
  const topic = 'mqtt/ufpb-'+options[0].value.toString()+'/temp'
  client.on('connect', () => {
    console.log('Connected to MQTT broker');
    client.subscribe('mqtt/ufpb-aq1/temp', { qos: 1 });
  });


  // '{"Bateria":90, "Temperatura":42, "Umidade":60}'
  client.on('message', (topic, message) => {
    setIncomingMessage(message.toString());
  });


  let resultado = incomingMessage;
  let bateriaMensagem;
  let temperaturaMensagem;   
  let umidadeMensagem;


  if(resultado){
    resultado = JSON.parse(resultado.toString());
    console.log(typeof(resultado.Bateria));
     bateriaMensagem = resultado.Bateria;
     temperaturaMensagem =resultado.Temperatura;   
     umidadeMensagem = resultado.Umidade;
  } 

  const messageColor = resultado != null ? 'green' : 'red';
  const batteryColor = batteryLevel <= 90 ? 'red' : 'green';
  const currentDate = new Date();
  const [dataSetOne, updateDataSetOne] = useState([0, 0, 0, 0, 0, 0]);
  const [dataSetTwo, updateDataSetTwo] = useState([0, 0, 0, 0, 0, 0]);



  // add side effect to component
  React.useEffect(() => {
    // create interval
    const interval = setInterval(
      // set number every 5s
      () => {
        const val1 = Math.floor(Math.random() * (50 - 0 + 1)) + 0;
        updateDataSetOne(prevData => {
          if(prevData.length >= 20) prevData.shift();
          console.log(prevData);
          return ([...prevData, val1]);
        });

        const val2 = Math.floor(Math.random() * (90 - 20 + 1)) + 20;
        updateDataSetTwo(prevData => {
          if(prevData.length >= 20) prevData.shift();
          console.log(prevData);
          return ([...prevData, val2]);
        });
      },
      5000
    );
    // clean up interval on unmount
    return () => {
      clearInterval(interval);
    };

  }, []);
  return (
    <div className="App">
      <header className="App-header">
        <h1>UFPB</h1>
      </header>
      <div className='main'>
        <div className='menus'>
          <div className='MensagemRecebida' style={{ backgroundColor: messageColor }}>
              <h3>Mensagem</h3>
          </div>

          <div className='node-wrapper'>
            <label htmlFor='nodes'>Node:</label>
            <Select options={options} defaultValue={options[0]} id="nodes"></Select>
          </div>
          <div className='battery' style={{ backgroundColor: batteryColor }}>
            <h3>Battery: {batteryLevel}%</h3>
          </div>

        </div>
        <div className='data'>
          <ChartViewer data={dataSetOne} title="Temperatura" />
          <ChartViewer data={dataSetTwo} title="Humidade" />
        </div>
      </div>
    </div>
  );
}

export default App;
